import { useEffect, useState } from "react"
import PortfolioList from "../portfolioList/PortfolioList"
import "./portfolio.scss"
import { featuredPortfolio, webPortfolio, mobilePortfolio, designPortfolio, contentPortfolio} from "../../data"




export default function Portfolio() {

    const [selected, setSelected] = useState("featured")
    const [data, setData] = useState([])

    const list= [
        {
            id : "featured",
            title : "Featured",
        },
        {
            id: "web",
            title: "Web App",
        },
        {
            id: "mobile",
            title: "Mobile App",
        },
        {
            id: "desing",
            title: "Desing",
        },
        {
            id: "contact",
            title: "Contact",
        },
    ]


useEffect(()=>{

    switch (selected) {
        case "featured":
            setData(featuredPortfolio)
            break;
        case "web":
            setData(webPortfolio)
            break;
        case "mobile":
            setData(mobilePortfolio)
            break;
        case "desing":
            setData(designPortfolio)
            break;
        case "contact":
            setData(contentPortfolio)
            break;

        default:
            setData(featuredPortfolio)
    }


},[selected])



    return (
        <div className="portfolio" id="portfolio">
            <h1>portfolio</h1>
            <ul>
             {list.map(li=>(
                 <PortfolioList li={li} active={selected === li.id} setSelected={setSelected} />
             ))}
            </ul>
            <div className="container">
   
    <div className="item">
                    <img src="https://global-uploads.webflow.com/5f16d69f1760cdba99c3ce6e/5fb3b3b19980844957f7e06b_Frame%2062.png"
                        alt=""

                    />
                    <h3>Banking App</h3>
                </div>
            </div>
        </div>
    )
}
